<script>

export default {
  name: 'Organisations',
  props: {
    data: {
      type: Object,
      required: true,
    },
  },
  data() {
    return {
      url: '/about-us/#membership-and-associations',
      iframeYoutubeSrc: '',
      liveURL: '',
      loadEager: 5,
    }
  },
  computed: {
    logos() {
      let logosSwiper = []
      let logosPinned = []

      if (this.data.logo) {
        this.data.logo.forEach((item) => {
          if (item.pin) {
            logosPinned.push(item)
          } else {
            logosSwiper.push(item)
          }
        })
      }
      return { swiper: logosSwiper, pinned: logosPinned }
    },
  },
  methods: {
    linkFilter(link) {
      const config = useRuntimeConfig()
      return link
        .toString()
        .replace(config.public.API_URL, '')
        .replace(config.public.DOMAIN_URL, '')
    },
    imageAnimateLoad(e) {
      e.currentTarget?.classList.remove('opacity-0')
    },
  },
}
